import React from 'react';
import { makeStyles } from '@mui/styles';
import "./style.css";
import card_shatter from "./partial_shatter.png";

const useStyles = makeStyles((theme) => ({
    itemOuter: {
        postiion: "relative",
        margin: "10px",
        // border: "solid 1px black",
        width: "209px",
        height: "294px",
        boxShadow: "5px 5px 5px black",
        borderRadius: "7px",
        transition: "transform .5s ease",
        "&:hover": {
            transform: "scale(1.1)",
        },
        "&:animate": {

        }
    },

    img: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
    },

    img2: {
        position: "relative",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        transition: "transform 0.5s ease",
        transform: "rotateX(15deg)",
    },

    img3: {
        position: "relative",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        transition: "transform 0.5s ease",
        transform: "rotateX(-15deg)",
    },

    cardBlank: {
        backgroundColor: "grey",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
    }
}));

const InventoryItem = ({ itemId, itemCount }) => {
    const classes = useStyles();

    if (itemCount > 1) {
        return (
            <div className={classes.itemOuter}>
                <img className={classes.img} src={card_shatter} alt="Card Back" />
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    } else {
        return (
            <div className={classes.itemOuter}>
                <img className={classes.img} src={card_shatter} alt="Card Back" />
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    }
}

export default InventoryItem;
