import React from 'react';
import { makeStyles } from '@mui/styles';
import "./style.css";
import card_shatter from "./partial_shatter.png";

const useStyles = makeStyles((theme) => ({
    itemOuter: {
        margin: "10px",
        // border: "solid 1px black",
        width: "210px",
        height: "295px",
        boxShadow: "5px 5px 5px black",
        borderRadius: "7px",
        transition: "transform 1s",
        "&:hover": {
            transform: "scale(1.2)",
        }
    },

    img: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
    },

    cardBlank: {
        backgroundColor: "grey",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
    }
}));

const InventoryItem = ({ itemData }) => {
    const classes = useStyles();

    return (
        <div className={classes.itemOuter}>
            <img className={classes.img} src={card_shatter} alt="Card Back" />
            {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                <img className={classes.img} src={card_back} alt="Card Back" />
                <img className={classes.img} src={card_shatter} alt="Card Back" />
            </ReactCardFlip> */}
        </div>
    )
}

export default InventoryItem;
