import React from 'react';
import { makeStyles } from '@mui/styles';
import { Badge } from "@mui/material";
import "./style.css";
import card_shatter from "./partial_shatter.png";
import card_front from "./card-front-non-texture.png";

const useStyles = makeStyles((theme) => ({
    itemOuter: {
        postiion: "relative",
        margin: "10px",
        // border: "solid 1px black",
        width: "209px",
        height: "294px",
        boxShadow: "5px 5px 5px black",
        borderRadius: "7px",
        transition: "transform .5s ease",
        zIndex: 200,
        "&:hover": {
            transform: "scale(1.1)",
        },
        "&:animate": {

        }
    },

    img: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        zIndex: 100,
    },

    img2: {
        position: "absolute",
        width: "210px",
        height: "295px",
        top: 10,
        borderRadius: "7px",
        transition: "transform 1s ease",
        transform: "rotateZ(5deg)",
        zIndex: 200,
    },

    img3: {
        position: "relative",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        transition: "transform 0.5s ease",
        transform: "rotateX(-15deg)",
    },

    cardBlank: {
        backgroundColor: "grey",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
    }
}));

const InventoryItem = ({ itemId, itemCount }) => {
    const classes = useStyles();

    if (itemCount > 1) {
        return (
            <div className={classes.itemOuter}>
                <Badge badgeContent={itemCount} color="secondary">
                    <img className={classes.img} src={card_front} alt="Card Back" />
                    <img className={classes.img2} src={card_shatter} alt="Card Front" />
                </Badge>
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    } else {
        return (
            <div className={classes.itemOuter}>
                <img className={classes.img} src={card_shatter} alt="Card Back" />
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    }
}

export default InventoryItem;
