import React from 'react';
import { makeStyles, styled } from '@mui/styles';
import { Badge, Fade, Popper, Button, Typography } from "@mui/material";
import "./style.css";
import card_shatter from "./partial_shatter.png";
import card_front from "./card-front-non-texture.png";
import viktor_frost from "./VIKTOR_FROST_HYPER.png";

const useStyles = makeStyles((theme) => ({
    itemOuter: {
        postiion: "relative",
        margin: "10px",
        // border: "solid 1px black",
        width: "208px",
        height: "293px",
        // boxShadow: "5px 5px 5px black",
        borderRadius: "7px",
        transition: "transform .5s ease",
        zIndex: 200,

    },

    img: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        zIndex: "100",
        position: "absolute",
        top: 10,
    },

    img2: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        boxShadow: "2px 5px 5px black",
        transition: "transform 0.25s ease",
        // transform: "rotateZ(5deg)",
        zIndex: "200",
        "&:hover": {
            transform: "scale(1.1)",
        },
        "&:animate": {

        }
    },

    img3: {
        position: "relative",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        boxShadow: "5px 5px 5px black",
        transition: "transform 0.25s ease",
        "&:hover": {
            transform: "scale(1.1)",
        },
    },

    countBadge: {
        position: "absolute",
        zIndex: "100",
        top: 0,
    },

    cardBlank: {
        backgroundColor: "grey",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
    },

    popper: {
        textAlign: "center",
    }
}));

const InventoryItem = ({ itemId, itemCount }) => {
    const classes = useStyles();
    const [cardView, setCardView] = React.useState(false);
    const [anchorEl, setAnchorEl] = React.useState(null);


    const StyledBadge = styled(Badge)(({ theme }) => ({
        '& .MuiBadge-badge': {
            right: -200,
            top: 295,
            border: `2px solid black`,
            padding: '0 4px',
        }
    }))

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
        setCardView(!cardView);
    }

    const canBeOpen = cardView && Boolean(anchorEl);
    const id = canBeOpen ? 'transition-popper' : undefined;

    if (itemCount > 1) {
        return (
            <div className={classes.itemOuter} onClick={(e) => handleClick(e)}>
                <Popper id={id} open={cardView} anchorEl={anchorEl} transition>
                    <div className={classes.popper}>
                        <Fade in={cardView} timeout={350}>
                            <img className={classes.img2} src={viktor_frost} alt="Card Front" />
                        </Fade>
                        <Button onClick={(e) => handleClick(e)}>
                            <Typography variant="h6">Close</Typography>
                        </Button>
                    </div>
                </Popper>
                <img className={classes.img2} src={viktor_frost} alt="Card Front" />
                {/* <img className={classes.img} src={card_shatter} alt="Card Back" /> */}
                <StyledBadge className={classes.countBadge} badgeContent={itemCount} color="info" max={99}>
                </StyledBadge>
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    } else {
        return (
            <div className={classes.itemOuter}>
                <img className={classes.img3} src={card_front} alt="Card Back" />
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    }
}

export default InventoryItem;
