import React from 'react';
import { makeStyles, styled } from '@mui/styles';
import { Badge, Typography } from "@mui/material";

// Various Card Types
import NormalCard from '../CardItem/NormalCard/NormalCard';
import HoloCard from '../CardItem/HoloCard/HoloCard';
import ShatterCard from '../CardItem/ShatterCard/ShatterCard';
import FullArtCard from '../CardItem/FullArt/FullArtCard';
import "./style.css";

const useStyles = makeStyles((theme) => ({
    itemOuter: {
        postiion: "relative",
        marginBottom: "25px",
        // border: "solid 1px black",
        width: "208px",
        height: "293px",
        // boxShadow: "5px 5px 5px black",
        borderRadius: "7px",
        transition: "transform .5s ease",
        zIndex: 200,

    },

    img: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        zIndex: "100",
        position: "absolute",
        top: 10,
    },

    img2: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        boxShadow: "2px 5px 5px black",
        transition: "transform 0.25s ease",
        zIndex: "200",
        "&:animate": {

        }
    },

    img3: {
        position: "relative",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        boxShadow: "5px 5px 5px black",
        transition: "transform 0.25s ease",
        // "&:hover": {
        //     transform: "scale(1.1)",
        // },
    },

    countBadge: {
        position: "absolute",
        zIndex: "100",
        top: 0,
    },

    cardBlank: {
        backgroundColor: "grey",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
    }
}));

const InventoryItem = ({ itemId, itemCount, itemType }) => {
    const classes = useStyles();

    const StyledBadge = styled(Badge)(({ theme }) => ({
        '& .MuiBadge-badge': {
            right: -97,
            top: 295,
            border: `2px solid black`,
            padding: '0 4px',
        }
    }))

    const StyledCounter = styled('div')({
        color: 'black',
        backgroundColor: 'white',
        border: "solid 2px black",
        // position: "absolute",
        width: "20%",
        paddingTop: 8,
        borderRadius: 10,
        top: 300,
        left: 85,
        marginLeft: "42%",
        marginTop: "-20px",
        zIndex: 500,
    })

    if (itemCount > 1) {
        return (
            <div className={classes.itemOuter} onClick={() => console.log(itemCount, itemType)}>
                {/* <img className={classes.img2} src={card_template} alt="Card Front" /> */}
                {/* <StyledBadge className={classes.countBadge} badgeContent={itemCount} color="info" max={99}>
                </StyledBadge> */}
                {/* <div className="card eevee animated"></div>
                <style className="hover"></style> */}
                {itemType === "normal" ?
                    <NormalCard />
                    : itemType === "holo" ?
                        <HoloCard />
                        : itemType === "shatter" ?
                            <ShatterCard />
                            // <FullArtCard />
                            : <></>
                }
                {/* <HoloCard /> */}
                <StyledCounter>
                    <Typography variant="body1">
                        {itemCount}
                    </Typography>
                </StyledCounter>
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    } else {
        return (
            <div className={classes.itemOuter} onClick={() => console.log(itemCount, itemType)}>
                {/* <img className={classes.img3} src={card_front} alt="Card Back" /> */}
                {/* <HoloCard /> */}
                {itemType === "normal" ?
                    <NormalCard />
                    : itemType === "holo" ?
                        <HoloCard />
                        : itemType === "shatter" ?
                            <ShatterCard />
                            : <>Error Unknown Type</>
                }
                {/* <NormalCard /> */}
                {/* <StyledCounter>
                    <Typography variant="body1">
                        {itemCount}
                    </Typography>
                </StyledCounter> */}
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    }
}

export default InventoryItem;
