import React, { useEffect } from 'react';
import { makeStyles } from '@mui/styles';
import "./style.css";
import card_back from "./card_back.png";
import card_shatter from "./partial_shatter.png";

import ReactCardFlip from "react-card-flip";

const useStyles = makeStyles((theme) => ({
    itemOuter: {
        margin: "10px",
        // border: "solid 1px black",
        width: "210px",
        height: "295px",
        // boxShadow: "5px 5px 5px black",
        borderRadius: "7px",
    },

    img: {
        width: "210px",
        height: "295px",
    }
}));

const InventoryItem = ({ itemData }) => {
    const classes = useStyles();
    const [flipped, setFlipped] = React.useState(false);

    return (
        <div className={classes.itemOuter} onClick={() => setFlipped(!flipped)}>
            {/* <img className={classes.img} src={card_back} alt="Card Back" /> */}
            <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                <img className={classes.img} src={card_back} alt="Card Back" />
                <img className={classes.img} src={card_shatter} alt="Card Back" />
            </ReactCardFlip>
        </div>
    )
}

export default InventoryItem;
