import React, { useState } from 'react';
import { makeStyles } from '@mui/styles';
import ReactCardFlip from 'react-card-flip';
import ReactAudioPlayer from 'react-audio-player';
import card_back from '../CardItem/TestCard/card_back.png';

import HoloCard from '../CardItem/HoloCard/HoloCard';
import ShatterCard from '../CardItem/ShatterCard/ShatterCard';
import NormalCard from '../CardItem/NormalCard/NormalCard';

const styles = makeStyles((theme) => ({
    flipCard: {
        margin: "25px",
    },
    cardBack: {
        transition: "box-shadow 0.1s ease-out",
        "&:hover": {
            boxShadow: "-20px -20px 30px -25px white, 20px 20px 30px -25px white, -7px -7px 10px -5px white, 7px 7px 10px -5px white, 0 0 13px 4px rgba(255,255,255,0.3), 0 55px 35px -20px rgba(0, 0, 0, 0.5)",
        }
    }
}))

const CardFlip = ({ cardImg, cardType }) => {
    const classes = styles();
    const [flipped, setFlipped] = useState(false);

    const handleFlip = () => {
        if (!flipped) {
            setFlipped(true)
        }
    }

    return (
        <ReactCardFlip isFlipped={flipped} flipDirection='horizontal' cardStyles={classes.flipCard}>
            <div onClick={() => handleFlip()} className={classes.cardBack}>
                <img src={card_back} alt="logo" style={{ width: "210px", height: "295px", position: "relative", margin: "10px" }} />
            </div>
            <>
                {/* <HoloCard /> */}
                {cardType === "holo" ? <HoloCard /> : cardType === "shatter" ? <ShatterCard /> : <NormalCard />}
                {/* <ShatterCard /> */}
            </>
        </ReactCardFlip>
    )
}

export default CardFlip
