import React from 'react';
import ReactCardFlip, { CardFlip } from "react-card-flip";
import { makeStyles } from "@mui/styles";
import card_back from "./card_back.png";

import HoloCard from '../CardItem/HoloCard/HoloCard';
import ShatterCard from "../CardItem/ShatterCard/ShatterCard";

const useStyles = makeStyles((theme) => ({
    main: {
        display: "none",
        transition: "transform 1s ease"
    },
    show: {
        display: "auto",
        // position: "absolute",
        // marginLeft: "45%",
        width: 600,
        height: 600,
        transition: "transform 1s ease"
    },
    packOuter: {
        display: "flex",
        justifyContent: "center",
    }
}))

const PacksFrame = () => {
    const classes = useStyles();
    const [isFlipped, setIsFlipped] = React.useState(false);

    const handleFlip = () => {
        if (!isFlipped) {
            setIsFlipped(true);
        }
    }

    return (
        <div className={classes.packOuter} onClick={() => handleFlip()}>
            <ReactCardFlip isFlipped={isFlipped} flipDirection='horizontal'>
                <img src={card_back} alt="backing" style={{ width: "210px", height: "295px", margin: "15px" }} />
                <div className={isFlipped ? classes.show : classes.main}>
                    {/* <HoloCard /> */}
                    <ShatterCard />
                </div>
            </ReactCardFlip>
        </div>
    )
}

export default PacksFrame;
