    //                 }
    //             />
    //         )
    //     } else {
    //         return (
    //             <Route
    //                 {...rest}
    //                 render={
    //                     <Redirect to={"/login"} />
    //                 }
    //             />
    //         )
    //     }
    // })
    return (
        <Route
            {...rest}
            render={routeProps =>
                !!currentUser ? (
                    <RouteComponent {...routeProps} />
                ) : (<Redirect to={"/login"} />)
            }
        />
    )

}

export default PrivateRoute;
