import { getDatabase, ref, child, get } from "firebase/database";

const dbRef = ref(getDatabase());
get(child(dbRef, `users`)).then((snapshot) => {
    if (snapshot.exists()) {
        return (snapshot.val())
    } else {
        console.log("No data available");
    }
}).catch((error) => {
    console.error(error);
});
