import { Card, CardActions, TextField, Typography, Button } from '@mui/material';
import { makeStyles } from '@mui/styles';
import React from 'react';

import { Link } from "react-router-dom";

import { getAuth, updateProfile } from "firebase/auth";

const useStyles = makeStyles((theme) => ({
    mainDiv: {
        width: "100%",
        maxWidth: "400px",
        // height: "428px",
        // maxHeight: "428px",
        textAlign: "center",
    },
    loginPaper: {
        width: "100%",
        padding: "25px",
        textAlign: "center",
    },
    loginPaperTitle: {
        marginBottom: "25px"
    },
    textField: {
        margin: "10px 10px 10px 10px"
    },
    submitButton: {
        margin: "10px"
    }
}))

const AccountDetails = () => {
    const classes = useStyles();

    return (
        <div style={{
            justifyContent: "center",
            display: "flex",
            padding: "35px",
        }}>
            <div className={classes.mainDiv}>
                <Card className={classes.loginPaper} elevation={5}>
                    <Typography variant="h4">Setting Up Your Account</Typography>
                    <TextField margin="normal" variant="outlined" label="Display Name" type="required" />
                    <TextField margin="normal" variant="outlined" label="Profile URL" type="required" />
                </Card>
            </div>
        </div>
    )
}

export default AccountDetails;
