import LootData from "./chances.json";

function generateCard() {
    var v, i;
    var totalChance = 0;
    var LootTable = LootData.chances;
    for (i = 0; i < LootTable; i++) {
        v = LootTable[i];
        if (v.weight > 0) {
            totalChance += v.weight;
        }
    }
    // var choice = 0;
    var randomNumber = Math.floor(Math.random(1, 100));
    console.log(randomNumber);
    // var weight = 0;
    // for(i = 0; i < LootTable.length; i++){
    //     v = LootTable[i]
    //     if (v.weight)
    // }
}

export default generateCard;
