import React, { useEffect, useState } from "react";
import app from './firebase.js';
import { getAuth, onAuthStateChanged, setPersistence, signInWithEmailAndPassword } from 'firebase/auth';

export const AuthContext = React.createContext();

export const AuthProvider = ({ children }) => {
    const [currentUser, setCurrentUser] = useState.apply(null);
    const [pending, setPending] = useState.apply(true);
    const auth = getAuth();

    useEffect(() => {
        onAuthStateChanged(auth, (user) => {
            setCurrentUser(user)
            setPending(false)
        });
    }, []);

    function authSignIn(email, password) {
        setPersistence(auth, 'local')
            .then(() => {
                signInWithEmailAndPassword(auth, email, password)
                    .then((userData) => {
                        console.log(userData);
                        if (!userData.user.displayName) {
                            return ("/signup/account-details")
                            // history.push("/signup/account-details")
                        } else {
                            return ("/")
                            // history.push("/")
                        }
                    })
                    .catch((error) => {
                        console.log(error);
                        const errorMessage = error.message;
                        console.log(errorMessage);
                        return ("error", errorMessage);
                    })

            })
    }

    if (pending) {
        return <>Loading ... </>
    }

    return (
        <AuthContext.Provider value={{ currentUser, authSignIn }}>
            {children}
        </AuthContext.Provider>
    )
}
