import React from 'react';
import { makeStyles, styled } from '@mui/styles';
import { Badge, Typography } from "@mui/material";
// import card_shatter from "./partial_shatter.png";
// import card_front from "./card-front-non-texture.png";
// import viktor_frost from "./VIKTOR_FROST_HYPER.png";
// import card_template from "./card_template.png";
// import Tilt from "react-tilt";

import $ from 'jquery';

import "./script.js";

// Various Card Types
import NormalCard from '../CardItem/NormalCard/NormalCard';
import HoloCard from '../CardItem/HoloCard/HoloCard';
// import ShatterCard from '../CardItem/ShatterCard/ShatterCard';
import "./style.css";

const useStyles = makeStyles((theme) => ({
    itemOuter: {
        postiion: "relative",
        // margin: "10px 10px 30px 10px",
        // border: "solid 1px black",
        width: "208px",
        height: "293px",
        // boxShadow: "5px 5px 5px black",
        borderRadius: "7px",
        transition: "transform .5s ease",
        zIndex: 200,

    },

    img: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        zIndex: "100",
        position: "absolute",
        top: 10,
    },

    img2: {
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        boxShadow: "2px 5px 5px black",
        transition: "transform 0.25s ease",
        // transform: "rotateZ(5deg)",
        zIndex: "200",
        // "&:hover": {
        //     transform: "scale(1.1)",
        // },
        "&:animate": {

        }
    },

    img3: {
        position: "relative",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
        boxShadow: "5px 5px 5px black",
        transition: "transform 0.25s ease",
        // "&:hover": {
        //     transform: "scale(1.1)",
        // },
    },

    countBadge: {
        position: "absolute",
        zIndex: "100",
        top: 0,
    },

    cardBlank: {
        backgroundColor: "grey",
        width: "210px",
        height: "295px",
        borderRadius: "7px",
    }
}));

const InventoryItem = ({ itemId, itemCount }) => {
    const classes = useStyles();

    var x;
    var $cards = $(".card");
    var $style = $(".hover");

    $cards
        .on("mousemove touchmove", function (e) {
            // normalise touch/mouse
            var pos = [e.offsetX, e.offsetY];
            e.preventDefault();
            if (e.type === "touchmove") {
                pos = [e.touches[0].clientX, e.touches[0].clientY];
            }
            var $card = $(this);
            // math for mouse position
            var l = pos[0];
            var t = pos[1];
            var h = $card.height();
            var w = $card.width();
            var px = Math.abs(Math.floor(100 / w * l) - 100);
            var py = Math.abs(Math.floor(100 / h * t) - 100);
            var pa = (50 - px) + (50 - py);
            // math for gradient / background positions
            var lp = (50 + (px - 50) / 1.5);
            var tp = (50 + (py - 50) / 1.5);
            var px_spark = (50 + (px - 50) / 7);
            var py_spark = (50 + (py - 50) / 7);
            var p_opc = 20 + (Math.abs(pa) * 1.5);
            var ty = ((tp - 50) / 3.0) * -1;
            var tx = ((lp - 50) / 2.5) * .5;
            // css to apply for active card
            var grad_pos = `background-position: ${lp}% ${tp}%;`
            var sprk_pos = `background-position: ${px_spark}% ${py_spark}%;`
            var opc = `opacity: ${p_opc / 100};`
            var tf = `transform: rotateX(${ty}deg) rotateY(${tx}deg)`
            // need to use a <style> tag for psuedo elements
            var style = `
      .card:hover:before { ${grad_pos} }  /* gradient */
      .card:hover:after { ${sprk_pos} ${opc} }   /* sparkles */ 
    `
            // set / apply css class and style
            $cards.removeClass("active");
            $card.removeClass("animated");
            $card.attr("style", tf);
            $style.html(style);
            if (e.type === "touchmove") {
                return false;
            }
            clearTimeout(x);
        }).on("mouseout touchend touchcancel", function () {
            // remove css, apply custom animation on end
            var $card = $(this);
            $style.html("");
            $card.removeAttr("style");
            x = setTimeout(function () {
                $card.addClass("animated");
            }, 2500);
        });

    const StyledBadge = styled(Badge)(({ theme }) => ({
        '& .MuiBadge-badge': {
            right: -97,
            top: 295,
            border: `2px solid black`,
            padding: '0 4px',
        }
    }))

    const StyledCounter = styled('div')({
        color: 'black',
        backgroundColor: 'white',
        border: "solid 2px black",
        // position: "absolute",
        width: "20%",
        paddingTop: 8,
        borderRadius: 10,
        top: 300,
        left: 85,
        marginLeft: "42%",
        marginTop: "-20px",
        zIndex: 500,
    })

    if (itemCount > 1) {
        return (
            <div className={classes.itemOuter} onClick={() => console.log(itemCount)}>
                {/* <img className={classes.img2} src={card_template} alt="Card Front" /> */}
                {/* <StyledBadge className={classes.countBadge} badgeContent={itemCount} color="info" max={99}>
                </StyledBadge> */}
                {/* <div className="card eevee animated"></div>
                <style className="hover"></style> */}
                <HoloCard />
                <StyledCounter>
                    <Typography variant="body1">
                        {itemCount}
                    </Typography>
                </StyledCounter>
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    } else {
        return (
            <div className={classes.itemOuter} onClick={() => console.log(itemCount)}>
                {/* <img className={classes.img3} src={card_front} alt="Card Back" /> */}
                {/* <HoloCard /> */}
                <NormalCard />
                <StyledCounter>
                    <Typography variant="body1">
                        {itemCount}
                    </Typography>
                </StyledCounter>
                {/* <ReactCardFlip isFlipped={flipped} flipDirection='horizontal'>
                    <img className={classes.img} src={card_back} alt="Card Back" />
                    <img className={classes.img} src={card_shatter} alt="Card Back" />
                </ReactCardFlip> */}
            </div>
        )
    }
}

export default InventoryItem;
